import React, { useState } from 'react';
import classnames from "classnames";
export default function RatingPage() {
  const [rating, setRating] = useState(null);
  const [hoverRating, setHoverRating] = useState(null);

  const handleRatingClick = (value) => {
    setRating(value);
  };

  const handleMouseEnter = (value) => {
    setHoverRating(value);
  };

  const handleMouseLeave = () => {
    setHoverRating(null);
  };
  return (
    <>
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h2>Rate this page</h2>
      {[...Array(5)].map((_, index) => {
        const ratingValue = index + 1;
        return (
          <span
            key={ratingValue}
            style={{ cursor: 'pointer', color:'blue' ,fontSize:50}}
            onClick={() => handleRatingClick(ratingValue)}
            onMouseEnter={() => handleMouseEnter(ratingValue)}
            onMouseLeave={handleMouseLeave}
          >
            {hoverRating ? (
              ratingValue <= hoverRating ?  <i className="nc-icon nc-favourite-28" style={style=fill}/>  : '☆'
            ) : (
              ratingValue <= rating ? '★' : '☆'
            )}
          </span>
        );
      })}
      <br />
      {rating ? <p>You rated this page: {rating} stars</p> : <p>Please rate this page</p>}
    </div>
    </>
  );
}
