// src/VehicleHireForm.js

import React, { useState } from 'react';
import axios from 'axios';
const vehicleOptions = {
    Bicycle: ['Mountain'],
    Motorcycle: ['Scooty', 'Pulser', 'CT 100', 'Discover'],
    Car: ['BMW', 'Alto', 'Wagon-R', 'Maruti Suzuki']
};

const VehicleHireForm = () => {
    const [formData, setFormData] = useState({
        vehicleName: '',
        vehicleType: '',
        capacity: '',
        hireDate: '',
        returnDate: '',
        customerName: ''
    });
    const [isSubmitted, setIsSubmitted] = useState(false);

    const handleVehicleNameChange = (e) => {
        const vehicleName = e.target.value;
        setFormData({
            ...formData,
            vehicleName: vehicleName,
            vehicleType: '' // Reset vehicle type when vehicle name changes
        });
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
            ...formData,
            [name]: value
        });
    };

   /* const handleSubmit = (e) => {
        e.preventDefault();
        // Process form data here (e.g., send to an API)
        console.log('Form Data:', formData);
        setIsSubmitted(true);
        // Reset form after submission
        setFormData({
            vehicleName: '',
            vehicleType: '',
            capacity: ''
            
            
        });
    };*/
    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Fetch user information from registrations collection
            const response = await axios.get(`http://localhost:5000/registrations`);
            console.log('User information:', response.data);
            // Assuming you have retrieved user information and stored it in state variables
            const { name, email, phone } = response.data;
            // Proceed with submitting suggestions along with user information
            await axios.post('http://localhost:5000/travellers', {
                
                name,
                email,
                phone
            });
            setSubmissionSuccess(true);
            setRating(null); // Reset rating after submission
            setSuggestions(''); // Clear suggestions input after submission
            alert('Suggestion submitted successfully');
        } catch (error) {
            console.error('Error submitting suggestion:', error);
            alert('Failed to submit suggestion. Please try again.');
        }
    };
    return (
        <div>
            {isSubmitted ? (
                <div className="success-message">
                    You reserved your vehicle successfully!
                </div>
            ) : (
                <form onSubmit={handleSubmit}>
                    <div>
                        <label>
                            Vehicle Name:
                            <select
                                name="vehicleName"
                                value={formData.vehicleName}
                                onChange={handleVehicleNameChange}
                                required
                            >
                                <option value="">Select Vehicle</option>
                                {Object.keys(vehicleOptions).map((name) => (
                                    <option key={name} value={name}>{name}</option>
                                ))}
                            </select>
                        </label>
                    </div>
                    <div>
                        <label>
                            Vehicle Type:
                            <select
                                name="vehicleType"
                                value={formData.vehicleType}
                                onChange={handleChange}
                                required
                                disabled={!formData.vehicleName}
                            >
                                <option value="">Select Type</option>
                                {formData.vehicleName && vehicleOptions[formData.vehicleName].map((type) => (
                                    <option key={type} value={type}>{type}</option>
                                ))}
                            </select>
                        </label>
                    </div>
                    <div>
                        <label>
                            Capacity:
                            <input
                                type="number"
                                name="capacity"
                                value={formData.capacity}
                                onChange={handleChange}
                                required
                            />
                        </label>
                    </div>
            
                    
                    <button type="submit">Submit</button>
                </form>
            )}
        </div>
    );
};

export default VehicleHireForm;
