import React, { useState } from 'react';
// reactstrap components
import { Button, Card, Form, Input, Container, Row, Col } from "reactstrap";

// core components
import ExamplesNavbar from "components/Navbars/ExamplesNavbar.js";
import Homenav from "components/Navbars/Homenav.js";

function RegisterPage() {
  document.documentElement.classList.remove("nav-open");
  React.useEffect(() => {
    document.body.classList.add("register-page");
    return function cleanup() {
      document.body.classList.remove("register-page");
    };
  });
  const [rating, setRating] = useState(null);
  const [hoverRating, setHoverRating] = useState(null);

  const handleRatingClick = (value) => {
    setRating(value);
  };

  const handleMouseEnter = (value) => {
    setHoverRating(value);
  };

  const handleMouseLeave = () => {
    setHoverRating(null);
  };
  return (
    <>
      <Homenav />
      <div
        className="page-header"
        style={{
          backgroundImage: "url(" + require("assets/img/ln2.jpg") + ")",
        }}
      >
        <div className="filter" />
        <Container>
          <Row>
            <Col className="ml-auto mr-auto" lg="4">
              <Card className="card-register ml-auto mr-auto">
              <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h2>Rate this page</h2>
      <label>Your Comment</label>
                  <Input placeholder="Type here" type="text" />
      {[...Array(5)].map((_, index) => {
        const ratingValue = index + 1;
        return (
          <span
            key={ratingValue}
            style={{ cursor: 'pointer', color:'yellow' ,fontSize:50}}
            onClick={() => handleRatingClick(ratingValue)}
            onMouseEnter={() => handleMouseEnter(ratingValue)}
            onMouseLeave={handleMouseLeave}
          >
            {hoverRating ? (
              ratingValue <= hoverRating ?  '★'  : '☆'
            ) : (
              ratingValue <= rating ? '★' : '☆'
            )}
          </span>
        );
      })}
      <br />
      {rating ? <p>You rated this page: {rating} stars</p> : <p>Please rate this page</p>}
    </div>
    <Button block className="btn-round" color="info" href="/home">
                   Submit
                  </Button>
              </Card>
            </Col>
          </Row>
        </Container>
        <div className="footer register-footer text-center">
          <h6>
            © {new Date().getFullYear()}, made with{" "}
            <i className="fa fa-heart heart" /> by Ravidu
          </h6>
        </div>
      </div>
    </>
  );
}

export default RegisterPage;
